// Extract the file extension and name of the resource.
msg.file = encodeURIComponent( msg.filename.split('\\').pop() );
msg.ext = msg.file.split('.').pop();

let myError = false;

switch(msg.ext)
{
    case 'OL-template':
        msg.path = 'template';
        break;
    case 'OL-datamapper':
        msg.path = 'DataMiningConfig';
        break;
    case 'OL-jobpreset':
        msg.path = 'JobCreationConfig';
        break;
    case 'OL-outputpreset':
        msg.path = 'OutputCreationConfig';
        break;
    default:
        myError = true;
}

if (false === myError ) {
    return [ msg, null ];
} else {
    msg.payload = null;
    return [ null, msg ];
}

//Fix: if there are any line breaks in between column values, then that column value will be wrapped in double quotes
//first get all the double quoted values list using regex
var lineBreakList = msg.payload.match(/(["'])(\\?)[\s\S]*?\1/g);
//search for line breaks using regex, if found replace it with empty value
lineBreakList.forEach(function (item) {
    msg.payload = msg.payload.replace(item, item.replace(/\r?\n|\r/g, '<br \/>'));
});

return msg;

